package main

import (
	"context"
	"fmt"
	"net/http"
	"os"

	"connectrpc.com/connect"
	"golang.org/x/net/http2"
	"golang.org/x/net/http2/h2c"

	greetv1 "github.com/davidmdm/connect-playground/internal/proto/greet/v1"        // generated by protoc-gen-go
	"github.com/davidmdm/connect-playground/internal/proto/greet/v1/greetv1connect" // generated by protoc-gen-connect-go
	"github.com/davidmdm/muxter"
)

type GreetServer struct {
	greetv1connect.UnimplementedGreetServiceHandler
}

var _ greetv1connect.GreetServiceHandler = GreetServer{}

func (s GreetServer) Greet(ctx context.Context, req *connect.Request[greetv1.GreetRequest]) (*connect.Response[greetv1.GreetResponse], error) {
	res := connect.NewResponse(&greetv1.GreetResponse{
		Greeting: fmt.Sprintf("Hello, %s!", req.Msg.Name),
	})
	res.Header().Set("Greet-Version", "v1")

	return res, nil
}

func main() {
	greeter := &GreetServer{}

	mux := muxter.New()

	mux.Use(muxter.Logger(os.Stderr, func(o muxter.RespOverview) string {
		return fmt.Sprintf("%s %s", o.Request.URL.Path, o.TimeElapsed)
	}))

	path, handler := greetv1connect.NewGreetServiceHandler(greeter)
	mux.Handle(path, muxter.Adaptor(handler, muxter.NoContext))

	// Use h2c so we can serve HTTP/2 without TLS.
	http.ListenAndServe(":8080", h2c.NewHandler(mux, &http2.Server{}))
}
